import React, { useEffect, useState } from "react";
import { Banner } from "./components/molecules";
import { Profile } from "./components/organisms";
import "./App.css";
import { db } from "./firebase";
import { getDoc, doc } from "firebase/firestore";

function App() {
  const [people, setPeople] = useState([]);
  const [total, setTotal] = useState();

  useEffect(() => {
    async function roadData() {
      const Ref = doc(db, "basic", "cards");
      const Snap = await (await getDoc(Ref)).data();

      console.log("Document data:", Snap);

      for (let i in Snap) {
        people.push({ i: Object.values(Snap[i]) });
      }

      setPeople(Snap);
    }
    roadData();
  }, []);

  console.log(people);

  return (
    <div className="App">
      {total && <Banner totalCnt={total} />}
      {people && (
        <>
          {Object.keys(people).map((item, index) => {
            <Profile
              key={index}
              id={item.id}
              url={item.url}
              des={item.des}
              name={item.name}
              job={item.job}
              isNew={item.isNew}
              totalSet={setTotal}
              like={item.like}
            />;
          })}
          {/* {people.map((item, index) => (
            <Profile
              key={index}
              id={item.id}
              url={item.url}
              des={item.des}
              name={item.name}
              job={item.job}
              isNew={item.isNew}
              totalSet={setTotal}
              like={item.like}
            />
          ))} */}
        </>
      )}
    </div>
  );
}

export default App;
