import React, { useState } from "react";
import { Button } from "../../atoms";

const LikeBox = () => {
  const [cnt, setCnt] = useState(0);

  const addCnt = () => {
    setCnt((prev) => prev + 1);
  };

  const minusCnt = (prev) => {
    if (prev > 0) {
      setCnt((prev) => prev - 1);
    }
  };
  return (
    <div>
      <span>{cnt}</span>
      <Button onClick={minusCnt} watchState={cnt} content="-" />
      <Button onClick={addCnt} watchState={cnt} content="+" />
    </div>
  );
};

export default LikeBox;
