import React, { useEffect, useState } from "react";
import { Banner } from "./components/molecules";
import { Profile } from "./components/organisms";
import "./App.css";
import { db } from "./firebase";
import {
  collection,
  getDocs,
  query,
  getDoc,
  setDoc,
  doc,
  docs,
  docSnap,
} from "firebase/firestore";

function App() {
  const [people, setPeople] = useState();
  const [total, setTotal] = useState();

  useEffect(() => {
    async function roadData() {
      const docRef = docs(db, "basic");
      const docSnap = await getDocs(docRef);

      console.log("Document data:", docSnap.data());
      setPeople(JSON.stringify(docSnap.data()));
    }
    roadData();
  }, []);

  console.log(people);

  return (
    <div className="App">
      {total && <Banner totalCnt={total} />}
      {people && (
        <>
          {people.map((item, index) => (
            <Profile
              key={index}
              id={item.id}
              url={item.url}
              des={item.des}
              name={item.name}
              job={item.job}
              isNew={item.isNew}
              totalSet={setTotal}
              like={item.like}
            />
          ))}
        </>
      )}
    </div>
  );
}

export default App;
