import React, { useState } from "react";
import { Button } from "../../atoms";

const LikeBox = () => {
  const [cnt, setCnt] = useState(0);
  const addCnt = () => {
    setCnt((cnt) => {
      cnt++;
    });
  };
  const minusCnt = () => {
    if (cnt > 0) {
      setCnt((prev) => {
        prev--;
      });
    }
  };
  return (
    <div>
      {cnt}
      <Button targetState={setCnt} content="-" />
      <Button targetState={setCnt} content="+" />
    </div>
  );
};

export default LikeBox;
